svg.script:
(function(){
  const svg = document.querySelector('svg');
  const group = document.getElementById('autoScale');

  // 获取 SVG 宽高
  const boxW = parseFloat(svg.getAttribute('width'));
  const boxH = parseFloat(svg.getAttribute('height'));

  // 原始设计尺寸
  const iconW0 = 700;   // 图标原始宽度 + 文字间距
  const iconH0 = 200;   // 图标原始高度
  const textW0 = 800;   // 文字宽度估算
  const textH0 = 200;   // 文字高度估算
  const totalW0 = iconW0 + textW0; 
  const totalH0 = Math.max(iconH0, textH0);

  // 计算缩放比例
  const scale = Math.min(boxW / totalW0, boxH / totalH0);

  // 计算居中偏移
  const xOffset = (boxW - totalW0*scale)/2;
  const yOffset = (boxH - totalH0*scale)/2;

  // 设置 transform
  group.setAttribute('transform', `translate(${xOffset},${yOffset}) scale(${scale})`);
})();
